highp float cellsize = 10.0;
float thickness = 0.05;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    highp float vertical = floor(thickness + mod(fragCoord.x, iResolution.x/cellsize) / (iResolution.x/cellsize));
    highp float horizontal = floor(thickness + mod(fragCoord.y, iResolution.y/cellsize) / (iResolution.y/cellsize));
    highp float val = horizontal + vertical;
    fragColor = vec4(horizontal, vertical, val, 1.0);
}
