#define IMAX   50.
#define SMOOTH 0

void mainImage(out vec4 O, vec2 U) {
  vec2 R = iResolution.xy;
  U = (U * 2. - R) / R.y;
  U.x -= .5; // for the sake of centrality

  vec2 zn = vec2(0);

  float i = 1.;
  for(; i <= IMAX && dot(zn, zn) < 6.; i++) {
    zn = mat2(zn, -zn.y, zn.x) * zn + U;
  }

  #if SMOOTH
  float zd = sqrt(dot(zn, zn));
  i -= log2(max(1., log2(zd)));
  #endif

  // color palette
  float t = i / IMAX;
  vec3 col = t * (1. +
             vec3(.5, 0, 1) *
             (1. - t));

  O = vec4(col, 1);
}
