 bool p(float n) {
        if (n <= 1.) return false;
        if (n <= 3.) return true;
        if (mod(n, 2.) == 0. || mod(n, 3.) == 0.) return false;
        float i = 5.;
        while (i * i <= n) {
            if (mod(n, i) == 0. || mod(n, i + 2.) == 0.) return false;
            i += 6.;
        }
        return true;
    }
void mainImage( out vec4 f, in vec2 fc )
{
    vec3 c = vec3(.1);
    fc.x += 50. * iTime;
    for(float x =0.; x <= fc.x;x++){
       c.r = p(x) ? 1. : 0.;
    }
    f = vec4(c,1.0);
}
