float rand(float i) {
    return fract(sin(i)*100000.);
}

float rand(float i, float n) {
    return fract(sin(i)*100000.*(1.+rand(n)));
}

float rand(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

float sm(float t) {
    return t*t*(3.-2.*t);
}

float noise(float i) {
    return mix(rand(floor(i)), rand(floor(i)+1.), sm(fract(i)));
}

float noise(float i, float n) {
    return mix(rand(floor(i), n), rand(floor(i)+1., n), sm(fract(i)));
}

void mainImage(out vec4 col, in vec2 pos) {
    vec2 st = pos/iResolution.yy;
    float ar = iResolution.x/iResolution.y;
    //float g = rand(rand(st)+iTime);
    float a = 0.;
    for(int i = 0; i < 9; ++i) {
        vec2 c = vec2(ar*noise(iTime, float(i)),noise(iTime+500.5, float(i)));   
        a += pow(1./length(c-st),1.);
    }
    float b = 0.;
    for(int i = 0; i < 9; ++i) {
        vec2 c = vec2(ar*noise(iTime, float(i)+100.),noise(iTime+500.5, float(i)+100.));   
        b += pow(1./length(c-st),2.);
    }
    col = vec4(vec2(a>=25.,b>=100.), .5, 1.);
}
