void mainImage( out vec4 U, in vec2 V )
{
    V = ( V + V - iResolution.xy ) / iResolution.y;
    U.wz = round( V / .4 ); // redefine round on webgl too slowly
    V -= .4 * U.wz;

    U = vec4(
        ( ( ( fract( sin( dot( U.wz, vec2( 1.2, 3.4 ) ) ) * .5 ) > .5 ) ? V.x : -V.x) + V.y > .0 ) ? 1. : 0.
    );
}
