float G(float x, float y, float sigma)
{
    return (1./(2.*3.1415*pow(sigma,2.)))*exp(-(pow(x,2.)+pow(y,2.))/(2.*pow(sigma,2.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0, uv);

    vec2 texelSize=1.0/vec2(textureSize(iChannel0,0));

    vec4 blur = vec4(0.);


    const int size=20;
    float sigma=10.;

    int ind = size/2;
    float sum=0.;
    float res[size*size];

    for (int x=-ind; x<= ind; x+=1)
    {
        for (int y=-ind; y<= ind; y+=1)
        {
            float val = G(float(x) , float(y) , sigma);
            sum+=val;

            vec2 offset = vec2(float(x), float(y))*texelSize;
            vec4 tCol=texture(iChannel0, uv+offset);

            blur+=tCol*val;
        }
    }

    blur/=sum;

    fragColor = vec4(blur);
}
