float sdSphere( vec3 p, float s){

    return length(p) - s;

}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.283185*(c*t+d) );
}


float sdChoob( vec3 p, float s, float k){

    float choob = pow(p.y,2.)/s + pow(p.z,2.)/s + 0.01 * float(p.x >  + k || p.x <  - k);

    return choob;

}

float smin( float a, float b, float k){

    float h = max( k-abs(a-b), 0.0)/k;

    return min( a,b ) - h*h*h*k*(1./6.);

}

float map(vec3 p){

    vec3 q = p;

    p.z += iTime * 2.5;

    q = fract(p) - 0.3;

    vec3 spherePos = vec3(0, 0, 0);

    float choob = sdChoob(q - spherePos, 2., 0.2 + 0.1 * sin(iTime));

    return choob;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.xy;

    //Guinitialization
    vec3 ro = vec3(0,0,-3.);
    vec3 rd = normalize(vec3(uv, 1));
    vec3 col = vec3(0);

    float t = 0.;

    int i;
    for(i = 0; i < 800; i++){
        vec3 p = ro + rd * t;

        p.y += sin(t * .2);
        p.x += cos(t * .2);

        float d = map(p);

        t += d;

        if (d < 0.007) break;
        if (t > 60.) break;


    }

    vec3 a = vec3 (0.5,0.2,0.5);
    vec3 b = vec3 (0.5,0.2,0.5);
    vec3 c = vec3 (0.8,0.3,0.5);
    vec3 d = vec3 (0.50,0.40,0.50);

    col = palette(t * .08 - float(i)*0.007,a,b,c,d);


    // Output to screen
    fragColor = vec4(col,0.5);
}
