// ğŸŒğŸŒ Equirexperiments
// License CC0-1.0
#define PI 3.1415926535897932384626433832795
#define TIME iTime

// Black Magic Function
float f(float x) {
  float y = fract(20. * abs(x));
  if (y < 0.1) return 1.0;
  return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

// Settings

    // How often to draw lines 
    float interval =.05;
    // Line thiccness
    float thicc = .005;


// Calculations

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy; 

    // Convert UV to 3D Point
    float alpha = 2.*PI*uv.x;
    float beta = PI*uv.y;
    vec3 equi = vec3(
        sin(beta)*cos(alpha),
        cos(beta),
        sin(beta)*sin(alpha)
    );

    // Calulate Spherical/Equirectangular UV
    vec2 sphericalUV = vec2(
        atan(equi.y, equi.x) / (2.0 * PI) + .5, 
        (equi.z+1.)*.5
    );

    // Dunno if this ever did anything useful but left un-used
    //float lat = atan(equi.z,equi.x);
    //float lon = acos(equi.y);
    //vec2  nuv = vec2(
    //    (lon + 3.14159265359) / (2.0 * 3.14159265359),
    //    lat / 3.14159265359
    //);


// Drawing    

    // Start with black
    vec3 col = vec3(0.0,0.0,0.0);

    // Create Checkerboard Pattern
    vec2 scaledUV = sphericalUV * 20.0;
    float checker = mod(floor(scaledUV.x) + floor(scaledUV.y), 2.0);
    // Paint Checkerboard Pattern
    col = mix(vec3(1.0, 1.0, 1.0), vec3(equi.x,equi.y,equi.z), checker);


    // If ??? something equirectangular-space
    // Don't know what this would be called (Max X || Y || Z == 1.0)?
    // Paints Light Red Lines
    float c = max(f(equi.x), max(f(equi.y), f(equi.z)));
    if(c==1.){
       col = vec3(1.,0.4,0.4);
    }

    // Draw animated purple gridlines based on UV (float offset used to animate)
    float offset = mod(iTime/10.,PI);
    if(mod(sphericalUV.x+offset,interval)<thicc || mod(sphericalUV.y+offset,interval)<thicc){
        col = vec3(1.,0.,1.);
    }


    // Output to screen
    fragColor = vec4(col,1.0);

}
