//170 - Thanks to SnoopethDuckDuck again
void mainImage(out vec4 o, vec2 u) {
    vec2 R = iResolution.xy,
         z,
         i;
    u = (u+u-R) / R.y;

    for (u.x -= .5; i.x++ < 1e2 && dot(z, z) < 4.; )
        z = u + mat2(z, -z.y, z.x) * z;

    o = .1 * sqrt(i.xxxx - log(length(z)-1.));
}


