void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    ivec2 uv = ivec2(fragCoord);
    ivec2 ir = ivec2(iResolution.xy);

    ivec2 ir2 = (ir * (1 + ((ivec2(50728129,77557187)*int(floor(iTime))) & 3))) / 6;

    uv -= ir2;
    uv = (uv*1002+uv.yx*ivec2(-1,1)*5)/1000;
    uv += ir2;
    fragColour = texelFetch(iChannel0,uv,0);
    if ( min(uv.x,uv.y) < 0 || max(uv.x-ir.x,uv.y-ir.y) >= 0 )
        fragColour = vec4(sin(vec3(7,8,9.7)*iTime),0);
    if ( iFrame == 0 ) fragColour = vec4(vec3((uv.y&32)/32),1);
}
