void mainImage(out vec4 c, vec2 v) {                // Simple Bricks 2.
    v *= .01;                                       // Resize the canvas.
    v += ceil(v.y *= 2.);                           // Offset and squish
    c = vec2(min(v = fract(v * .5),.9)!=v, 1).yxxy; // Display the color.
}                                                   // By Rik Riesmeijer.
