vec4 stuff(vec2 coord, float time) {
    float angle = atan(coord.y, coord.x);
    angle = mod(angle + 2. * time, (2. * 3.1415926 / 3.));
    if (2. * length(coord) < cos(angle)) {
        return (vec4(1.0, 1.0, 1.0, 1.0));
    } else {
        return (vec4(0.0, 0.0, 0.0, 1.0));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = min(iResolution.x, iResolution.y);
    vec2 coord = fragCoord/m - 0.5;

    float time = iTime;
    fragColor = stuff(coord, time);
    fragColor += stuff(coord.yx - vec2(0., 0.78), time + 1.3);    
}
