#define PI 3.14159265358

mat2 rotateMat(float theta) {
    return mat2(
        cos(theta), -sin(theta),
        sin(theta), cos(theta)
    );
}

float arg(vec2 polar) { // [-0.5PI, 1.5PI]
    if (polar.x == 0.0 && polar.y >= 0.0) return PI / 2.0;
    if (polar.x == 0.0 && polar.y < 0.0) return - PI / 2.0;
    float ang = atan(polar.y / polar.x);
    if (polar.x < 0.0) ang += PI;
    return ang;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    vec2 centor = vec2(0.5, 0.5);
    vec2 polar = uv - centor;
    float rho = length(polar);
    float theta = arg(polar);

    if(theta > PI / 2.0){
        theta = PI - theta;
    }

    float psi = theta > - PI / 2.0 * 0.12 - 0.21 * sin(iTime) ? theta + sin(iTime) : theta + 0.3 * sin(iTime);

    vec2 pos1 = centor + vec2(rho * cos(psi), rho * sin(psi));
    vec2 pos2 = centor + vec2(rho * cos(theta), rho * sin(theta));


    vec3 col1 = texture(iChannel0, pos1+ vec2(t * 0.04)).xyz;
    vec3 col2 = texture(iChannel1, pos2).xyz;
    vec3 col = mix(col1, col2, smoothstep(PI / 2.0 * 0.78, PI / 2.0 * 0.88, theta));
    col = mix(col2, col, smoothstep(- PI / 2.0 * 0.95, -PI / 2.0 * 0.90, theta));
    fragColor = vec4(col,1.0);
}
