void mainImage(out vec4 fragColor, vec2 fragCoord) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

float hash12(vec2 p) {
    return fract(sin(dot(p, vec2(2.718, 1.414))) * 3141.5);
}

#define g(x, y) texelFetch(iChannel0, ivec2(c) + ivec2(x, y), 0)
void mainImage(out vec4 o, vec2 c) {
    ivec4 n = ivec4(g(-1, -1) + g(-1, 0) + g(-1, 1) + g(0, -1) + g(0, 1) + g(1, -1) + g(1, 0) + g(1, 1)); 

    ivec4 s = ivec4(g(0, 0)) | n;
    o = vec4(s.x+s.z == s.y * int(s.z + 1), s.y+s.w == s.z * int(s.w - 1), s.z == s.w * int(s.x + 1), s.w + s.z*s.w == s.x * int(s.y - 2));
    if (iMouse.z > 0.0 || iFrame == 0) {
        float a = hash12(c);
        float b = hash12(c + vec2(100));
        float c = hash12(c - vec2(100));
        float d = hash12(c + vec2(200));
        o = vec4(a, b, c, d);
    }
}
