float sdBox( vec2 p, vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle( vec3 circle, vec2 pos )
{
    float d = length(pos-circle.xy)-circle.z;
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    // Circle: vec3(centerX, centerY, radius)
    vec3 circle = vec3(0., 0., 0.3);
    vec2 box = vec2(0.2, 0.3);

    float d = sdBox(uv, box);
    float c = sin(d*5. + iTime);
    float sm_c = smoothstep(0., 0.01, c);

    vec3 col = vec3(0.5, fract(c-0.5)+sm_c/2., 0.3);

    // Output to screen
    fragColor = vec4(col,1.0);
}
