#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}


void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,s=.2; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*2.;
    p.z-=2.5;p.zx*=R2D(iTime);p.yz*=R2D(iTime);

    for(j=0.;j++<2.;)
        p=1./abs(abs(p*cross(p,q)))*cos(s),
        d=dot(1.05,dot(p,p)),
        d-=length(p.xyx)/1e4;        
    e-=d;

}o.rga+=hsv(cos(i),.8,.6)*5./i;
}
