// From Danilo Guanabara, some modification.
// https://www.shadertoy.com/view/XsXXDn
// CC-BY-SA 3.0
// Some exploration about animation density, visible area and color brightness.

#define t iTime
#define r iResolution.xy

// Defined but not useful, since the existance of `l` can not be replaced.
vec2 unitvec(in vec2 p){return p/length(p);}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<3;i++) {
		vec2 uv,p=fragCoord.xy/r;
		uv=p;
		p-=.5;
		p.x*=r.x/r.y;
        p.xy*=.5;
		z+=.2;
		l=length(p);
		uv+=p/l*(sin(z)+.5)*floor(sin(l*9.-z));
		c[i]=.02/length(mod(uv,1.)-.5);
	}
	fragColor=vec4(c/l,t);
}
