renderpass.code:
vec2 offset( float t )
{
    return 0.56*cos( vec2(0.0,1.5708) + 0.1*t )
         - 0.2*cos( vec2(0.0,1.5708) + 0.2*t );
}

float Julia ( vec2 z, vec2 bias )
{
	float f = 1.;
	for( int i=0; i<128; i++ ) 
	{
		z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + bias;
		f = min( f, dot(z,z) );
	}
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float ratio = 4./3.; 
	vec2 p = ratio*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
	vec2 m = ratio*(-iResolution.xy+2.0*iMouse.xy   )/iResolution.y;

	float f, duration = 0.5; 
    float timex = 2.*duration*(iMouse.x / iResolution.x)-1.;
    float timey = 2.*duration*(iMouse.y / iResolution.y)-1.;
    f = Julia ( p, offset( iTime * timex * timey ) );
    f = 0.9+log(f)/10.0;
    vec3 color = vec3( f, f*f, f ); 
    color *= smoothstep( 0.004, 0.006, abs(p.x-m.x) );

    float q = length(m - p) - 0.02;
    if (q < 0.0) fragColor = vec4(vec3(1.0, 0.0, 0.0), 1.0);
	else fragColor = vec4(color,1.0);
}
