// the following code is altered from https://www.shadertoy.com/view/4llXD7 and https://www.shadertoy.com/view/3ltSW2

float sdMagic( in vec2 p, in float r) 
{
    vec2 q = abs(p)+r-0.3;
    return length(q) - r;
}
// big arc*4->little circle*4
// with r(t) decrease(r(t)-0.3 > 0 ? arc : circle)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
     uv -= 0.5;
     uv.x *= iResolution.x/iResolution.y;

    float ra = 0.7*(0.5 - 0.5*cos(2.0*iTime));
    float d0 = 1.0;
    uv.x += 1.0;

    for(int i = 0; i < 5; i++)
    {
       d0 *= sdMagic( uv, ra );
	   d0 *= sdMagic( uv-0.3, ra );
	   d0 *= sdMagic( uv+0.3, ra );
       uv.x-=0.6;
    }

	vec3 col = vec3(step(0.0, d0) );

	fragColor = vec4(col,1.0);
}
