#define iM iMouse
#define R iResolution
#define aspect R.x/R.y

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float zoomamnt = (iM.z > 1.) ? iM.y/R.y : .5;
    float fisheyeamnt = (iM.z > 1.) ? iM.x/R.x : .5;

    uv-=0.5;
    uv.x *= aspect;
    uv += mix(vec2(0.), uv, mix(0., 2., 0.5-zoomamnt)); // zoom

    if(iM.x/R.x > .5) {
        uv *= mix(1., length(uv.xy), -mix(0., 1., .5-fisheyeamnt)*2.); // fisheye
    } else {
        uv /= mix(1., length(uv.xy)*2., mix(1., 0., fisheyeamnt*2.)); // antifisheye
    }
    uv.x /=aspect;
    uv+=0.5;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;


    // Output to screen
    fragColor = vec4(col,1.0);
}
