/*
by Dom Mandy in 2024
*/
void mainImage(out vec4 P, vec2 V) {
    float v, x, y, z = y = x = 9.;
    vec2 Z = iResolution.xy,
         C = V = 2. * (V + V - Z).yx / Z.y;

    for (int k; k < 99; k++) {
        V = vec2(dot(V, C), dot(vec2(V.y, -V.x), C)) / dot(C, C) - C;

        x = min(x, abs(V.x));
        y = min(y, abs(V.y));
        z > (v = length(V)) ? z = v, Z = V : Z;
    }

    z = smoothstep(1., -7., log(x)) * smoothstep(1., -7., log(y));
    P = z + sqrt(z + z * cos(atan(Z.y, Z.x) - vec4(0, 2.1, 4.2, 0)));
}
