float luminance(vec3 color) {
    return dot(color, vec3(0.2126, 0.7152, 0.0722));
}

vec3 normalMap(vec2 uv, sampler2D tex, vec2 texelSize, float scale) {
	vec2 d = vec2(
        luminance( texture(tex, uv + vec2(texelSize.x, 0)).rgb ) -
        luminance( texture(tex, uv - vec2(texelSize.x, 0)).rgb ),
        luminance( texture(tex, uv + vec2(0, texelSize.y)).rgb ) -
        luminance( texture(tex, uv - vec2(0, texelSize.y)).rgb )
    );

    return .5+.5*normalize(vec3(-d*scale, 1.));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 ts = 1. / iChannelResolution[0].xy;

    vec3  LIGHT_COLOR    = vec3(1., 1., 1.);
    vec3  AMBIENT_COLOR  = vec3(0., 0., 0.);
    vec3  SPECULAR_COLOR = vec3(1., 1., 1.);
    vec3  DIFFUSE_COLOR  = texture(iChannel0, uv).rgb;
    float SHININESS      = 64.;

    vec3 p = vec3(uv, 0.);
    vec3 LightPos = vec3( mouse, .3);
    vec3 ViewPos  = vec3(.5, .5, 4.);

    vec3 LightDir = normalize( LightPos - p );
    vec3 ViewDir  = normalize( ViewPos  - p );
    vec3 HalfDir  = normalize( LightDir + ViewDir );

    vec3 Normal = normalMap(uv, iChannel0, ts, 5.);
    Normal = 2.*Normal-1.;

    float diffuseFactor  = max(dot(Normal, LightDir), 0.);
    vec3  diffuse  = DIFFUSE_COLOR  * LIGHT_COLOR * diffuseFactor;

    float specularFactor = pow(max(dot(Normal, HalfDir), 0.), SHININESS);
    vec3  specular = SPECULAR_COLOR * LIGHT_COLOR * specularFactor;

    vec3  color = AMBIENT_COLOR + diffuse + specular;

    fragColor = vec4(color, 1.);
}
