#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.02),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.02),
                    vec2(1.0)-_st);
    return uv.x*uv.y;
}

float xcross(in vec2 _st, float _size){
    return  box(_st, vec2(_size,_size/4.)) +
            box(_st, vec2(_size/4.,_size));
}

vec2 animSpace(in vec2 _st, float spinSpeed, float rotateRadius, float rotateSpeed) {
    vec2 translate = vec2(cos(iTime*rotateSpeed),sin(iTime*rotateSpeed));
    vec2 st = _st + translate*rotateRadius;
    st -= vec2(0.5);
    st = rotate2d(fract(iTime*spinSpeed)*PI) * st;
    st += vec2(0.5);
    return st;
}

float form(vec2 st, int i) {
    //                          spinSpeed        radi rotateSpeed           xsize
    return xcross(animSpace(st, float(i+1)*0.001, 0.5, 1.0-(float(i)*0.02)), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = ((fragCoord * 2.0 - iResolution.xy) / (iResolution.y*2.))+.5;
    float pct = 0.0;

    for (int i = 0; i<50; i++){
        float h = mod(float(i), 2.0);
        h == 0.0 ?
            pct += form(st, i):
            pct -= form(st, i);
    }

    vec3 color = mix(vec3(0.929,0.91,0.871), vec3(0.0), clamp(pct, 0.0, 1.0));

    fragColor = vec4(color,1.0);
}
