vec3 h(vec3 c, float s){
    return c * mat3(c += .33 - (c = vec3(cos(s), s = -sin(s) * .6, -s)).x / 3., c.zxy, c.yzx);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord/iResolution.xy;

    uv *= iResolution.xy;
    uv -= iResolution.xy * 0.5;
    uv /= iResolution.y;

    float dist = length(uv);
    vec3 circleColor = h(vec3(0.9, 0.5, 0.2), atan(uv.x, uv.y));
    float gradientFactor = smoothstep(0.1, 1.25, dist);
    vec3 finalColor = mix(circleColor, vec3(0.0), gradientFactor);

    fragColor = vec4(finalColor, 1.0);
}
