void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    float t = 1.5 * iTime;
    float exponent = 4.0;
    float maxI = 5.0;
    float blurScale = 0.1;
    float slimeScale = 1.2;
    vec3 slimeTint = vec3(0.6,1.0,0.5);
    float val1 = distance(uv,vec2(cos(t),sin(t)));
    float val2 = pow(uv.x,exponent) + pow(uv.y,exponent) + slimeScale;
    bool foo = distance(uv,vec2(cos(t),sin(t))) >= pow(uv.x,exponent) + pow(uv.y,exponent) + 1.25;
    vec3 col;
    vec2 offset = (1.0 + 0.1 *(val1 * val2)) * vec2(cos(t),sin(t));
    if(val1 >= val2)
    {
        for(float i;i<maxI;i++)
        {
            col += texture(iChannel0,0.5*(uv + offset + blurScale * vec2(cos(i), sin(i)))).x;
        }
        col *= slimeTint/maxI;
    }
    else if(val1 >= val2 - 0.1)
    {
        col += slimeTint*0.5*texture(iChannel0,exp(val1*uv)).x;
    }
    fragColor = vec4(col,1.0);
}
