void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0,uv);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * 0.1;

    float n1 = noise(uv + t * 0.5);
    float n2 = noise(uv - t * 0.5);
    float n3 = noise(uv + t);
    float n4 = noise(uv - t);

    float nx = (n1 + n2) * 0.5 + 0.5;  // [0,1]
    float ny = (n3 + n4) * 0.5 + 0.5;  // [0,1]

    vec2 vec = vec2(nx, ny);    
    fragColor = vec4(vec, 0.0, 1.0);
}

vec4 getNextColor(vec2 dir, sampler2D channel, vec2 uv)
{
    dir = (dir * 2.0) - 1.0;

    vec2 new_uv = uv - dir * clamp(0.0005/log(iTime),0.0015,10.0);
    new_uv = clamp(new_uv, vec2(0.0), vec2(1.0));

    return texture(channel, new_uv);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 vec = texture(iChannel1, uv).xy; 

    vec4 img = texture(iChannel0, uv);
    vec4 next_color = getNextColor(vec, iChannel0, uv);

    if (iTime < 1.0) {
        next_color = texture(iChannel2, uv);
    }

    fragColor = next_color;
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}


float noise(vec2 st) { // gradient noise
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}
