#define NORMALIZE

const float pi = 3.14159265359;


const float steps = 24.;
const float bias = 0.2;
const float roughness = 0.;
const float morphspeed = 0.025;
const float scrollspeed = 0.;

mat2 rot(float t)
{
    float c = cos(t);
    float s = sin(t);
    return mat2(c, -s, s, c);
}

void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 2. * (2. * I - iResolution.xy) / iResolution.y + scrollspeed * iTime;
    vec2 v = vec2(0.);
    for(float i = 0.; i < steps; i++)
    {
        v += abs(fract((uv + i * (5. + iTime * morphspeed)) * rot(i * (2. * pi / steps))) - 0.5);
        v = abs((1. + roughness) * v - bias);
    }
    #ifdef NORMALIZE
        v = normalize(v) - 0.5;
        v *= 2.;
    #endif
    vec3 col = vec3(length(v) * 0.25);
    O = vec4(col,1.0);
}
