// comment-out to see entire nested spheres structure
#define SHOW_ONLY_LEAF_SPHERES

vec2 hash21(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec3 hash31(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

vec4 hash41(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

// Heap navigation:
//     up = (index-1)>>1
//     left = (index<<1)+1
//     right = (index<<1)+2

//#define BOUNDING_SPHERE_LEVELS 1u
#define BOUNDING_SPHERE_LEVELS 6u
#define BOUNDING_SPHERE_NODE_COUNT ((1u << BOUNDING_SPHERE_LEVELS) - 1u)
#define BOUNDING_SPHERE_LEAF_BASE ((1u << (BOUNDING_SPHERE_LEVELS - 1u)) - 1u)
#define BOUNDING_SPHERE_LEAF_COUNT (1u << (BOUNDING_SPHERE_LEVELS - 1u))

vec4 bounding_spheres[BOUNDING_SPHERE_NODE_COUNT]; // ugly global variable in case array arguments are copied instead of being passed by reference.

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 500u

#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

vec3 random_unit_vector(float seed) // based on https://math.stackexchange.com/questions/44689/how-to-find-a-random-axis-or-unit-vector-in-3d
{
    vec2 rnd = hash21(seed);
    float phi = tau*rnd.x;
    vec2 polar = vec2(cos(phi),sin(phi));
    float z = 2.0*rnd.y-1.0;
    return vec3(sqrt(1.0-z*z)*polar,z);
}

vec4 enclosing_sphere(vec4 s0, vec4 s1) // https://www.shadertoy.com/view/lX2yzc enclosing sphere pair (jt)
{
    float d = length(s1.xyz - s0.xyz);

    if(d < s0.w - s1.w) return s0; // special case: second circle contained in first
    if(d < s1.w - s0.w) return s1; // special case: first circle contained in second

    float t = (s0.w + d + s1.w) / 2.0;

    return vec4(mix(s0.xyz, s1.xyz, (t - s0.w) / d), t);
}

vec2 bounding_sphere_intersect(vec3 ro, vec3 rd, vec4 s) // modified https://iquilezles.org/articles/intersectors/
{
    vec3 oc = ro - s.xyz;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - s.w*s.w;
    float h = b*b - c;
    if(h < 0.0) return vec2(DIST_MAX); // no intersection
    h = sqrt(h);
    return vec2(-h, +h) - b;
}

bool bounding_sphere_inside(vec3 p, vec4 s)
{
    return length(p-s.xyz) <= s.w+EPSILON;
}

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float sphere(vec3 p, vec4 s)
{
    return length(p-s.xyz) - s.w;
}

float box(vec3 p, vec3 b) // https://iquilezles.org/articles/distfunctions/
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float cylinder(vec3 p, float r, float h) // based on https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float longitudes(uint n, vec3 p) // https://www.shadertoy.com/view/DlVBzy latitudes and longitudes (jt)
{
    p.xy = p.yx; // hack to align with interior
    float slice = 2.0*pi/float(n);

    float mu = atan(p.x,p.y);
    float mu0 = floor(mu/slice)*slice;
    float mu1 =  ceil(mu/slice)*slice;
    mat3 m0 = mat3(sin(mu0),cos(mu0),0,cos(mu0),-sin(mu0),0,0,0,1);
    mat3 m1 = mat3(sin(mu1),cos(mu1),0,cos(mu1),-sin(mu1),0,0,0,1);

    vec3 q0 = m0 * p;
    vec3 q1 = m1 * p;

    return min(length(vec2(length(q0.xz)-1.0, q0.y)),length(vec2(length(q1.xz)-1.0, q1.y)));
}

float latitudes(uint n, vec2 p) // https://www.shadertoy.com/view/DlVBzy latitudes and longitudes (jt)
{
    n = n*2u-1u;

    float slice = 2.0*pi/float(n);

    float mu = atan(p.x,p.y);
    float mu0 = floor(mu/slice)*slice-slice/2.0;
    float mu1 =  ceil(mu/slice)*slice-slice/2.0;
    vec2 c0 = vec2(sin(mu0),cos(mu0));
    vec2 c1 = vec2(sin(mu1),cos(mu1));

    return min(length(p-c0),length(p-c1));
}

float latitudes(uint n, vec3 p)
{
    return latitudes(n, vec2(length(p.xy),p.z));
}

float grid_sphere(vec3 p, float r) // https://www.shadertoy.com/view/DlVBzy latitudes and longitudes
{
    return min(longitudes(12u,p/r)*r,latitudes(12u,p/r)*r);
}

float grid_sphere(vec3 p, vec4 s)
{
    return grid_sphere(p - s.xyz, s.w);
}

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

vec4 intersect(vec4 a, vec4 b)
{
    return a.w > b.w ? a : b;
}

vec4 offset(vec4 c, float d)
{
    return vec4(c.xyz,c.w-d);
}

vec4 map_switch_internal(vec3 p, uint index)
{
/*
    switch(index)
    {
    }
*/
    return vec4(vec3(0), DIST_MAX);
}

vec4 map_specific(vec3 p, uint index)
{
    vec4 combined = vec4(vec3(0), DIST_MAX);

    // debug
    //if(iMouse.z > 0.0 || iTime < 15.0) // enable at beginning for preview image, later on mouse-click
#ifdef SHOW_ONLY_LEAF_SPHERES
        if(index >= BOUNDING_SPHERE_LEAF_BASE) // show only leaf spheres
#endif
            if(bounding_spheres[index].w > 0.0)
                combined = combine(combined, vec4(0.5+0.5*bounding_spheres[index].xyz,grid_sphere(p,bounding_spheres[index]-vec4(0,0,0,0.02)) - 0.01)); // NOTE: shrink sphere slightly to make sure it is inside bounding sphere

    return combine(combined, map_switch_internal(p, index));
}

vec4 map_generic(vec3 p)
{
    vec4 combined = vec4(vec3(0), DIST_MAX);

    uint index_stack[BOUNDING_SPHERE_NODE_COUNT]; // reserve size for worst case: entire tree
    uint stack_pointer = 0u;
    index_stack[stack_pointer++] = 0u; // start at root

    while(stack_pointer > 0u)
    {
        stack_pointer--;
        uint index = index_stack[stack_pointer];

        if(bounding_sphere_inside(p, bounding_spheres[index]))
        {
            combined = combine(combined, map_specific(p, index));

            if(index < BOUNDING_SPHERE_LEAF_BASE) // node is not a leaf
            {
                index_stack[stack_pointer++] = (index<<1u)+1u;
                index_stack[stack_pointer++] = (index<<1u)+2u;
            }
        }
    }

    return combined;
}

// based on https://iquilezles.org/articles/normalsSDF tetrahedron normals
