// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).w < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map_generic(ro + rd*t).w;
        if(h < EPSILON)
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// based on https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO(vec3 pos, vec3 nor, uint index)
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map_generic(pos + h*nor).w;
        occ += (h-d)*sca;
        sca *= 0.95;
        if(occ > 0.35) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

void mainImage(out vec4 o, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (I + I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0,-4.0);
    vec3 rd = normalize(vec3(I.xy, 2.5)); // NOTE: omitting normalization results in clipped edges artifact

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;

    //vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    vec3 lightdir = normalize(vec3(2.0,1.0, 3.0));

    if(false) // try subdivision
    {
        bounding_spheres[0u] = vec4(vec3(0,0,0), 2);
        for(uint index = 0u; index < BOUNDING_SPHERE_LEAF_BASE; index++)
        {
            vec4 s = bounding_spheres[index];
            vec3 v = random_unit_vector(float(index)*123.456);
            vec2 rnd = hash21(float(index)*543.21);
            float r0 = s.w*mix(0.6,0.8,rnd.x);
            float r1 = s.w*mix(0.6,0.8,rnd.y);
            bounding_spheres[(index << 1u) + 1u] = vec4(s.xyz + v*(s.w - r0), r0);
            bounding_spheres[(index << 1u) + 2u] = vec4(s.xyz - v*(s.w - r1), r1);
        }
    }
    else // direct approach: results in inefficient bounding sphere structure - still faster than naive rendering
    {
        for(uint i = 0u; i < BOUNDING_SPHERE_LEAF_COUNT; i++)
        {
            bounding_spheres[BOUNDING_SPHERE_LEAF_BASE + i] = mix(vec4(-1,-1,-1,0.1),vec4(1,1,1,0.2),hash41(float(i)*123.456));
        }

        // construct enclosing spheres
        //for(uint level = BOUNDING_SPHERE_LEVELS - 1u; level > 0u; level--)
        for(uint j = 1u; j < BOUNDING_SPHERE_LEVELS; j++)
        {
            uint level = BOUNDING_SPHERE_LEVELS - j;
            uint node_count = (1u << (level - 1u));
            uint node_base = (1u << (level - 1u)) - 1u;
            uint leaf_base = (1u << level) - 1u;
            for(uint i = 0u; i < node_count; i++)
            {
                bounding_spheres[node_base + i] = enclosing_sphere(bounding_spheres[leaf_base + 2u * i + 0u],bounding_spheres[leaf_base + 2u * i + 1u]);
            }
        }
    }

    vec4 result = trace(ro, rd, 0.0, DIST_MAX);
    vec3 color = result.xyz;
    float dist = result.w;
    if(dist < DIST_MAX)
    {
        vec3 dst = ro + rd * dist;
        //vec3 n = normal(dst); // standard normals
        vec3 n = normal(ro + rd * (dist-EPSILON)); // reduce edge-glint by normal-backoff as suggested by elenzil

        //color = n * 0.5 + 0.5;

        vec3 ambient = vec3(0.1);
        float brightness = max(dot(lightdir, n), 0.0) * 2.0;
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 25.0);
        color *= (ambient * calcAO(dst, n, 0u/*index*/)) + brightness;

        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }

        vec3 fog_color = vec3(0);
        color = mix(fog_color, vec3(color), exp(-pow(dist/20.0, 2.0))); // fog
    }
    else
    {
        color = vec3(0);
    }
//    #if BOUNDING_SPHERE_LEAF_BASE + 3u == 6u
//    color = vec3(0,1,0);
//    #endif
    color = tanh(color); // roll-off overly bright colors
    color = sqrt(color); // approximate gamma

    //color += hash32(I*123.0)/255.0; // dither to remove banding in the background

    o = vec4(color, 1);
}
