// from https://shadertoy.com/view/X3lyW2
// from https://shadertoy.com/view/sdd3R4

//#define rot(a)    mat2( cos(a+vec4(0,11,33,0)) )            // rotation                  
  #define rot(a)  mat2(cos(a),sin(a),-sin(a),cos(a))
#define hue(v)  ( .6 + .6 * cos( v  + vec4(0,23,21,0)  ) )    // hue

void mainImage(out vec4 O, vec2 U) {    
    float t=9.,d, l, i, k = 2., // .9                         // tile multiplier
          R0 = 20., R1 = 10.;   // 40 x 23 tiles              // large and small radii

    vec3  R = iResolution,
          D = normalize( vec3( U+U, -3.5*R.y ) - R ),         // ray direction
          p = 90./R, q,                                       // marching point along ray 
       // M =  iMouse.xyz/R -.5;
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(8,4,0)/1e2*cos(iTime+vec3(0,11,0));
    O *= 0.;
    if ( T(50).w < 0. ) O = hue(30.*iTime);
    for ( ; i++ < 1e2 ; ) {                                   // --- ray-marching
        q = p,
        q.yz *= rot( .5+6.*M.y),                              // rotations
        q.xz *= rot( 2.-6.*M.x),
        l += t = abs( length(vec2(d=length(q.xz)-R0,q.y)) - R1); // abs for inside + outside

        float a = atan(q.z,q.x), b = atan(q.y,d),             // base torus parameterization
                                                              // --- conformal mapping
        r = R1/R0, ir = sqrt(1.-r*r);                         // antiderivative of 1/circonf(b) 
        b = .996*  2./ir* atan( (r-1.)/ir* tan(b/2.) );       //  NB: tan(b/2) = ( -d + sqrt(d*d+q.y*q.y) )/q.y )        

        q = vec3( k*R0*a/6.283, round(k*R1*b/6.283), 0 );     // cell id
        a = sin(k*R0*a/2.), b = cos(k*R1*b/2.);
        d = sqrt( 1. - min(abs(a)/fwidth(a),1.) * min( abs(b)/fwidth(b),1.) ) / exp( (l-80.)/30. ); // color: mesh * depth darkening
        if ( t < .01 ) 
          { O += d;                                           // draw mesh
            d = T(mod(q.xy,S)).x;                             // cell content
            if ( d > 0. ) O.g ++;
            if ( d < 0. ) O.r ++;
            t = .2;                                           // traverse the layer and continue
          }
        p += t*D;                                             // step forward = dist to obj          
    }
}

#define key(a) (texelFetch(iChannel3,ivec2(a,1),0).x > 0.)      // LEFT:37  UP:38  RIGHT:39  DOWN:40 
#define H(t)    fract(sin(mod( (t) *vec2(12.9898, 78.233 ), 6.28)) * 3758.5453)

vec2 new() {                                                    // --- create a new pills
    vec2 P;
    for ( float i; i++ < 50.; ) {
        P = floor( S* H(iTime - i*17.971) );
        if ( T(P.xy).x == 0. && P != T(50).xy ) return P;       // ok if free cell
    }
    return vec2(-1);
}

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    O = T(U);                                                   // restore state
    if (iFrame < 1 || T(50).w <  1. ) {                         // --- init
        if  ( T(50).w < 0. ) {                                  // wait 2" after loosing
            if ( U == vec2(50) )  O.w += 1.;
            return;
        }
        O *= 0.;
        if ( U == vec2(50) )                                    // init snake pos,dir,length
            O = vec4( floor(S*H(iTime)),  floor(4.*H(iTime+.5).x), 5 );
        if ( U == new() ) O.x = -1.;                            // set a pills
        return;
    }

    if ( U == vec2(51) ) O.x = key(37) ? 1. : key(39) ? -1. : O.x, // cumulates key event between pause
                         O.y += iTimeDelta;                     // to control the pause time
 // if (iFrame%10 > 0) return;                                  // slow game
    if (T(51).y < .17 ) return;                                 // slow game
    if ( U == vec2(50) ) {                                      // --- snake control
        O.z = mod( O.z + T(51).x, 4. );                         // user changes direction
     // O.z = mod( O.z + float(key(37)) - float(key(39)), 4. ); // user changes direction
        O.xy = mod( O.xy + mat4x2( 1,0, 0,1, -1,0, 0,-1 )[int(O.z)] , S ); // pos += dir
        if ( T(O.xy).x < 0. ) O.w++;                            // eat pills
        if ( T(O.xy).x > 0. ) O.w = -120.;                      // cross itself: loose !
    }
    else if ( U == vec2(51) ) O-=O;
    else if( U.x < S.x && U.y < S.y ) {                         // --- grid management
        vec4 P = T(50);                                         // snake state
        if (O.x > 0.) O.x--;                                    // snake trail
        if ( U==P.xy ) O.x = P.w;                               // snake head: timer = length
        if ( T(P.xy).x < 0. && U==new() ) O.x = -1.;            // pills eaten: new one            
    }
}
