void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;


     float d = length(uv);

     vec3 col = vec3(3.,1.,1.);

     vec2 center = vec2(0.0, 0.0); // Center of the ellipse
     vec2 radius = vec2(0.6, 0.3); // Radius in x and y directions

     vec2 pos = (uv - center) / radius;

     float ecl = dot(pos, pos);

     ecl = sin(ecl*.4 - iTime)/50.;
     ecl = abs(ecl);

     ecl = .02 /ecl;

      if (ecl <= 1.2) {
        col = vec3(5.0, 1.0, 2.0); // Ellipse color (white)
    } else {
        col = vec3(5.0, 5.0, 5.0); // Outside color
    }


     d = sin(d*8. - iTime)/8.;

     d = .02 /d;

     col *=d;

     fragColor = vec4(col,1.0);

}
