float hash(float n) { return fract(sin(n) * iTime); }

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f*f*(3.0-2.0*f);
    float n = dot(i, vec2(1.0, 57.0));
    return mix(mix(hash(n +  0.0), hash(n +  1.0), f.x),
               mix(hash(n + 57.0), hash(n + 58.0), f.x), f.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * 0.1;

    vec3 col = vec3(0.1, 0.1, 0.9);

    float r = length(uv);
    float a = atan(uv.y, uv.x);

    float f = 0.5 + 0.5 * sin(10.0 * r - 10.0 * t + a);
    f = f * 0.5 + 0.5 * noise(uv * 3.0 + vec2(t));

    col = mix(col, vec3(1.0, 0.5, 0.2), f);
    col = mix(col, vec3(0.2, 0.5, 1.0), f);

    col += abs(sin(iTime)+0.2)*(0.03/ r);

    fragColor = vec4(col, 1.0);
}
