#define PI 3.14159265359
vec3 colorSun = vec3(1.0,0.45,0.35);
vec3 palette(in float t)
{
    vec3 a = vec3(0.598 ,0.408, 0.408);
    vec3 b = vec3(-0.242 ,0.028, -0.092);
    vec3 c = vec3(0.988 ,-2.312 ,-1.122);

    vec3 d = vec3(-0.932 ,-0.972, -2.392);



    return a + b*cos(6.28318*(c*t+d));
}


float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = (fragCoord*1.0-iResolution.xy)/iResolution.y;

    // Time varying pixel color
     vec3 col = palette(length(uv.y)+0.01*sin(iTime*PI));

    // Output to screen
    vec4 layer3 = vec4(colorSun, 1.0-(step(0.0,sdCircle( vec2(uv.x+0.4,uv.y+0.55), 0.03 ))));

    vec4 layer1 = vec4(col, 1.0);

    fragColor = mix(layer1, layer3, layer3.a);
}
