vec3 avgColor = vec3(0.769, 0.641, 0.534);
vec3 skyColor = vec3(0.9, 0.6, 0.4);  // Simulated sky color
vec3 waterColor = vec3(0.2, 0.3, 0.5); // Simulated water color
vec3 sunColor = vec3(1.0, 0.8, 0.0);  // Simulated sun color


vec3 colorA = vec3(0.149,0.141,0.912);
vec3 colorB = vec3(1.000,0.833,0.224);


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    // 定义颜色

    // 定义混合因子
    float factor1 = smoothstep(0.0, 0.5, st.y);
    float factor2 = smoothstep(0.5, 1.0, st.y);

    // 第一个渐变：从colorA到colorB
    vec3 gradient1 = mix(skyColor, waterColor, factor1);

    // 第二个渐变：从colorB到colorC
    vec3 gradient2 = mix(sunColor, skyColor, factor2);

    // 合并两个渐变
    color = mix(gradient1, gradient2, step(0.3, st.y));

    // 将合并后的颜色与平均颜色进行混合
    color = mix(color, avgColor, 0.5);

    fragColor = vec4(color, 1.0);
}


