const float offset = 1.5;
const float mortarStart = 0.35;
const float mortarEnd = 0.6;
const float width = 1.;
const float height = 1.5;
const vec3 color1 = vec3(0.5, 0.25, 0.25);
const vec3 color2 = vec3(0.4, 0.15, 0.1);
const vec3 colorMortar = vec3(0.6, 0.6, 0.5);
void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 2. * (2. * I - iResolution.xy) / iResolution.y;
    vec2 uvBrick = uv;
    uvBrick *= vec2(width, height);
    vec2 modFloor = mod(floor(uvBrick), 2.);
    uvBrick.x += offset * modFloor.y;
    vec2 uvMortar = 1. - abs(fract(uvBrick) - 0.5);
    float mortar = 1. - min(uvMortar.x, uvMortar.y);
    mortar = smoothstep(mortarStart, mortarEnd, mortar);
    vec3 col = mix(color1, color2, mod(floor(uvBrick.x), 2.));
    col = mix(col, colorMortar, mortar);
    O = vec4(col,1.0);
}
