vec3 func(vec3 c) {
    c = vec3(c.x - float(int(c.x)), c.y - float(int(c.y)), c.z - float(int(c.z)));
    return 1.5-abs(4.0*c - 2.5);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float hue = iTime*0.123, angle = iTime*0.32132;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = func(hue + vec3(0, 0.33333, 0.666666));
    col *= cos(100.0*dot(vec2(cos(angle), sin(angle)), uv-vec2(0.5, 0.5))) < 0.0 ? 1.0 : 0.5;
    fragColor = vec4(col, 1.0);
}
