vec2 cell = vec2(0.2, 0.2);

vec2 point(int x, int y) {
    int i = (x + y * 2) % 5;
    float r = 0.5 + 0.3 * sin(float((i * 123) % 9 + 4) * iTime * 0.05 + 0.5);
    float a = float((i * 345) % 7 + 4) * iTime * 0.05 + r;
    return cell * vec2(x, y) + 0.05 * r * vec2(cos(a), sin(float((i * 143) % 3 + 2) * a));
}

float switch_interval = 2.0;
float dist(vec2 a, vec2 b) {
    float T = 2.0; // mod(iTime, switch_interval * 6.) / switch_interval;
    switch(int(T)) {
    case 0: return abs(a.x-b.x) + abs(a.y-b.y); // 1-d "manhattan"
    case 1:
    case 2: 
    case 3: 
    case 4: return pow(pow(abs(a.x-b.x), T) + pow(abs(a.y-b.y), T), 1./T);
    case 5: return max(abs(a.x-b.x), abs(a.y-b.y)); // inf-d ""
    }
}

float BORDER_R = 0.01;
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float min_d = 666.;
    vec2 min_c = p;
    float hilite = 0.;
    float border = 0.;
    int ix = int(p.x / cell.x);
    int iy = int(p.y / cell.y);
    for (int idy = -1; idy < 2; idy++) {
        for (int idx = -1; idx < 2; idx++) {
            // if (idx == 0 && idy == 0) { continue; }
            vec2 c = point(ix + idx, iy + idy);
            float d = dist(c, p);
            hilite += d < 0.05 ? d : 0.0;
            float dd = d - min_d;
            if (dd < BORDER_R) {
                if (dd < -BORDER_R) {
                    min_d = d;
                    min_c = c;
                    border = 0.0;
                } else {
                    border = 1.0 - abs(dd) / BORDER_R;
                }
            }
        }
    }

    vec3 col = 0.5 + hilite + 0.5*cos(iTime+min_c.xyx+vec3(0,2,4)) - border;

    fragColor = vec4(col,1.0);
}
