vec3 oklab_to_rgb(vec3 color) {
    float l = dot(vec3(1.0, 0.3963377774, 0.2158037573), color);
    float m = dot(vec3(1.0, -0.1055613458, -0.0638541728), color);
    float s = dot(vec3(1.0, -0.0894841775, -1.2914855480), color);

    l = l * l * l;
    m = m * m * m;
    s = s * s * s;

    return sqrt(vec3(
        4.0767416621f * l - 3.3077115913f * m + 0.2309699292f * s,
        -1.2684380046f * l + 2.6097574011f * m - 0.3413193965f * s,
        -0.0041960863f * l - 0.7034186147f * m + 1.7076147010f * s
    ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    vec3 col = oklab_to_rgb(vec3(1.0, uv));
    fragColor = vec4(col, 1.0);
}
