void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Base color
    vec3 col = vec3(0.0);

    // Base space (x-y plane)
    float baseSpace = smoothstep(0.01, 0.0, abs(uv.y));
    col += vec3(0.2, 0.2, 0.8) * baseSpace;



    // my(x) function (wavy line)
    float t = iTime * 0.5;
    float myX = sin(uv.x * 5.0 + t) * 0.2;
    float myXLine = smoothstep(0.01, 0.0, abs(uv.y - myX));
    col += vec3(1.0, 0.2, 0.2) * myXLine;

    // Dyke paths (zig-zag pattern)
    float dykePath = step(0.9, sin(uv.y * 50.0 + uv.x * 10.0 + t * 2.0));
    col += vec3(1.0, 0.7, 0.0) * dykePath * 0.3;

    // Horizontal spaces (small discs)
    vec2 diskUV = fract(uv * 5.0) - 0.5;
    float disks = 1.0 - smoothstep(0.1, 0.11, length(diskUV));
    col += vec3(0.9, 0.9, 0.9) * disks * 0.3;

    // p-adic homomorphism (curved line on base)
    float pAdic = smoothstep(0.01, 0.0, abs(uv.y - 0.1 * sin(uv.x * 3.0 + t)));
    col += vec3(0.8, 0.2, 0.8) * pAdic;

    // NP-hard structure (complex pattern above base)
    float npHard = sin(uv.x * 10.0) * sin(uv.y * 10.0 + t) * sin(uv.x * uv.y * 20.0 + t);
    npHard = smoothstep(0.2, 0.3, npHard);
    col += vec3(0.7, 0.7, 0.7) * npHard * step(0.0, uv.y);

    // Fibers (vertical lines)
    float fibers = smoothstep(-100.01, 15.0, abs(sin(uv.x/uv.y)));
    col += vec3(0.1, 0.1, 0.1) * fibers * (1.0 - baseSpace)-npHard*sin(t);

    // Output final color
    fragColor = vec4(col, 1.0);
}
