vec3 feverDreamPalette(float t) {
    vec3 a = vec3(0.6, 0.0, 0.8);
    vec3 b = vec3(0.3, 0.0, 0.5);
    vec3 c = vec3(0.9, 0.3, 1.0);
    vec3 d = vec3(0.2, 0.7, 0.4);

    return a + b * sin(6.28318 * (c * t + d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    float t = iTime * 0.8;
    float angle = atan(uv.y, uv.x);
    float radius = length(uv);
    angle += sin(radius * 5.0 - t * 2.0) * 0.6;
    radius += sin(t + radius * 3.0) * 0.1;
    uv = vec2(cos(angle), sin(angle)) * radius;
    float ripple = cos(radius * 12.0 - t * 4.0) * exp(-radius * 1.5);
    vec3 color = feverDreamPalette(radius + ripple);
    float flicker = sin(iTime * 10.0 + uv.x * 5.0) * 0.1;
    color += flicker * 0.3;
    fragColor = vec4(color, 1.0);
}
