#define p_nvt(n_add, n_mul)(sin(n_add+iTime*n_mul)*.5+.5)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/iResolution.y;
    o_trn *=1.;


    float nv_t = (sin(iTime)*.5+.5);
    float n_tau = 6.2831;
    float n_its = 3.;
    float n_d_prod = 1.;
    for(float n_it = 0.; n_it < n_its;n_it+=1.){
        float n_it_nor = n_it / n_its;
        vec2 o_amp = vec2(1.)*sin(iTime*.2+n_it_nor);
        vec2 o_p = vec2(
            sin(n_it_nor * n_tau), 
            cos(n_it_nor * n_tau)
        )*o_amp;
        float n_d = length(o_trn -o_p);
        n_d = sin(n_d*10.);
        n_d_prod += n_d;
    }
    float n_d1 = sin(n_d_prod*4.+iTime*2.2);
    float n_d2 = sin(n_d_prod*2.+iTime*2.2);
    float n_d3 = sin(n_d_prod*1.+iTime*2.2);

    fragColor = vec4(n_d1,n_d2,n_d3,1.);
}
