float circle( in vec2 _st, float _radios){
vec2 dist = _st-vec2(0.0);
return 1.-smoothstep( _radios- ( _radios*0.01),
_radios + ( _radios*0.5),
dot(dist,dist) * 30.0);
}

vec2 rotate(in vec2 p, in float t){

return mat2(
sin(t), cos(t),
cos(t) , -sin(t)
) *p;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float t = iTime ;
vec2 uv = gl_FragCoord.xy/ iResolution.xy;
uv -= 0.5;
uv = rotate(uv*fract( circle(uv,.49) ),t);
   fragColor = vec4(vec3(fract(uv.xy*uv.yx),fract(uv.xy*uv.yx)),1.0);
}
