void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, -10.0); // Camera position
    vec3 rd = normalize(vec3(uv, 1.0)); // Ray direction

    vec3 col = vec3(0.0);

    // Central planet
    float t = 0.0;
    for (int i = 0; i < 64; i++) {
        vec3 p = ro + rd * t;
        float d = length(p) - 2.0; // Distance to central planet (radius 2.0)
        if (d < 0.01) {
            vec3 pos = ro + rd * t;
            vec3 nor = normalize(pos);
            vec2 texCoord = vec2(atan(nor.x, nor.z) / (2.0 * 3.14159265) + 0.5, acos(nor.y) / 3.14159265);
            col = texture(iChannel3, texCoord).rgb;
            break;
        }
        t += d;
    }

    // Orbiting planets
    for (int i = 0; i < 3; i++) {
        float angle = iTime * 0.5 + float(i) * 2.0 * 3.14159265 / 3.0; // Offset angles for each planet
        float radius = 4.0 + float(i) * 2.0; // Orbit radius
        vec3 center = vec3(cos(angle), 0.0, sin(angle)) * radius;

        t = 0.0;
        for (int j = 0; j < 64; j++) {
            vec3 p = ro + rd * t;
            float d = length(p - center) - 1.0; // Distance to orbiting planet (radius 1.0)
            if (d < 0.01) {
                vec3 pos = ro + rd * t;
                vec3 nor = normalize(pos - center);
                vec2 texCoord = vec2(atan(nor.x, nor.z) / (2.0 * 3.14159265) + 0.5, acos(nor.y) / 3.14159265);
                if (i == 0) col = texture(iChannel0, texCoord).rgb;
                else if (i == 1) col = texture(iChannel1, texCoord).rgb;
                else if (i == 2) col = texture(iChannel2, texCoord).rgb;
                break;
            }
            t += d;
        }
    }

    fragColor = vec4(col, 1.0);
}
