void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0; 
    uv.x *= iResolution.x / iResolution.y; 

    float time = iTime * 0.5;
    float zoom = 1.0 + 0.5 * sin(time);

    float angle = time; 
    float c = cos(angle);
    float s = sin(angle);
    mat2 rotation = mat2(c, -s, s, c);

    uv *= rotation;
    uv /= zoom; 

    vec3 bgColor = vec3(229.0 / 255.0, 222.0 / 255.0, 202.0 / 255.0); 

    float wave = sin(uv.y * 10.0 + time) * 0.1;
    uv.x += wave;

    float radius = length(uv);
    float pattern = sin(radius * 15.0 - time * 5.0);

    vec3 color1 = vec3(222.0 / 255.0, 185.0 / 255.0, 199.0 / 255.0); 
    vec3 color2 = vec3(187.0 / 255.0, 45.0 / 255.0, 100.0 / 255.0); 

    vec3 spiralColor = mix(color1, color2, smoothstep(0.0, 1.0, pattern));

    fragColor = vec4(mix(bgColor, spiralColor, smoothstep(0.0, 1.0, radius)), 1.0);
}
