// actual PML code
void mainImage(out vec4 o, vec2 fc)
{
    ivec2 ij = ivec2(fc);
    if (ij.y != 0) discard;
    vec2 uv = fc / R;

    if (iFrame == 0) // || texelFetch(iChannel0, ivec2(0), 0).w != R.x)
    {
        float p0 = 0.;
        // something about an initial wave reflects back
        //p0 = .01 * sin(24. * 3.1416 * uv.x); // * sin(7.*3.1416*fc.y/R.y);
        //p0 = .5 * smoothstep(.95, .9,length(uv));
        o = vec4(p0, 0, 0, R.x);
        return;
    }
    // no .5 offset here - tried, did bad things
    float ax = alphax_at(vec2(ij), R);

    float h = Delta / (2. * c_max);

    float e1x = ax > 0. ? exp(-ax * h / kappa) : 1.;
    float e2x = ax > 0. ? (1. - e1x) / (Delta * ax) : h / (Delta * kappa);
    ivec2 di = ivec2(1, 0);
    float px_np1 = Px(ij) * e1x - e2x * (Ux(ij) - Ux(ij - di));
    // it's a Laplacian operator dropped to 1D

    vec2 ps = pressureSource(uv.x, float(iFrame));
    /*if (iMouse.z > 0.) // no interaction (yet)
    {
        float d = length(fc - iMouse.zw);
        float source = smoothstep(30., 0.0, d);
        px_np1 += source;
        py_np1 += source;
    }*/
    // "At the outer boundary a perfectly reflecting boundary condition is enforced"
    if ( ij.x == 0 
      || ij.x == int(R.x) - 1)
    {
        // Zero pressure on boundary
        o = vec4(0, 0, 0, R.x);
    }
    else if (ps.y > 0.)
    {
        o = vec4(ps.x * .5, 0, 0, R.x);
    }
    else
    {
        o = vec4(px_np1, 0, 0, R.x);
    }
}

// PML part B from http://shadertoy.com/view/fsyfWt - U buffer (position/amplitude)

// U aka ğ˜‚ is a vector velocity field, but in 1D it's just scalar.
/*
// no PML, all 1.0 constants removed for clarity
void mainImage2(out vec4 o, vec2 fc)
{
    ivec2 ij = ivec2(fc);
    if (ij.y != 0) discard;

    if (iFrame == 0) {
        o = vec4(0, 0, 0, R.x);
        return;
    }

    ivec2 di = ivec2(1, 0);
    // pressure (partial) derivative
    float ux_np1 = Ux(ij) - 1./2. * (Px(ij + di) - Px(ij));

    o = vec4(ux_np1, 0, 0, R.x);
}
*/
// with PML
void mainImage(out vec4 o, vec2 fc)
{
    ivec2 ij = ivec2(fc);
    if (ij.y != 0) discard;

    if (iFrame == 0) // || texelFetch(iChannel1, ivec2(0),0).w != R.x)
    {
        float p0 = 0.;
        // an initial wave reflects back.  hrmm.
        //p0 = .1 * sin(24. * 3.1416 * fc.x/R.x); // * sin(7.*3.1416*fc.y/R.y);
        o = vec4(p0, 0, 0, R.x);
        return;
    }

    float h = Delta / (2. * c_max);
    // why + .5? seems crucial.
    float gx = alphax_at(vec2(ij) + vec2(.5, 0), R) / kappa; // gamma ğ›¾ Î³
    // paper discusses gamma in terms of the boundary layer index.
    // Î³_x(i,j) = Î±_max((m-i-1/2)/m)Â², i = 0,...,m-1, j = m,...,N_y-m-1
    // ğš« is the spatial sample interval.
    // Î³_x(i,j) = Î±_x((i+1/2)ğš«,jğš«)/Îº((i+1/2)ğš«,jğš«) // 2D eqn
    // Î³_x(i)   = Î±_x((i+1/2)ğš«   )/Îº((i+1/2)ğš«) // chopped down 3D eqn to 1D :)
    // Ï_x(i,j) = Ï((i+1/2)ğš«,jğš«)

    float e3x = gx > 0. ? exp(-gx * h) : 1.;
    float e4x = gx > 0. ? (1. - e3x) / (Delta * rhox * gx) : h / (Delta * rhox);
    ivec2 di = ivec2(1, 0);
    float ux_np1 = Ux(ij) * e3x - e4x * (Px(ij + di) - Px(ij));

    o = vec4(ux_np1, 0, 0, R.x);
}

// from http://shadertoy.com/view/fsyfWt

// pretty good w m = 3. now in 1D
const float m = 4.; // PML thickness
// original toy used 8. for 2D.
// seems can get away with fewer in 1D.
// I believe the imbalance problem was in alphax_at ... now fixed, hopefully.

const float
    alphamax = 2.3, // Î±
    Delta = 1., // might be dt?  ğš«t, fixed framerate stuff
    c_max = 1., // wave speed limit = 1 grid cell / frame
    kappa = 1., // Ï
    rhox = 1.; // Îº
// all these constants that are 1.0 clutter up the code visually.
// too bad we can't use actual greek letters in code!  would help.

#define sqr(x) ((x) * (x))
#define R iResolution.xy

#define Px(ij) texelFetch(iChannel0, ij, 0).x
#define Ux(ij) texelFetch(iChannel1, ij, 0).x

float alphax_at(vec2 c, vec2 r)
{
    float i = min(c.x, r.x - 1. - c.x);  // bugfix right boundary, improves balance
    i = min(i, m); // FIXME the paper pg 819 says i = 0,...,m-1 so this clamp seems wrong, off by 1
    return alphamax * sqr((m - i) / m); // the squaring seems important
}

