void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;uv-=0.7;

    uv.x -= sin(iTime/3.);

    float sun = length(uv);
    sun = 0.001/smoothstep(0.,1.,sun);

    vec3 x = -abs(vec3(uv.x));
    vec3 y = -abs(vec3(uv.y));

    vec3 xy = mix(x,y,1.1);

    vec3 col = vec3(0);
    col += 1./vec3(1.,2.,1.);

    col += sun;
    col += xy;

    vec3 stars = 0.01/(1.-vec3(texture(iChannel0,uv)));

    col += stars;

    fragColor = vec4(col,1.0);
}
