// BASED ON XOR CHROMATIC DERIVATICES: https://www.shadertoy.com/view/4f2fWc

#define RADIUS 8.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float CHROMATIC = 2.0;

    vec4 color = texture(iChannel0,uv);

    vec4 mip = texture(iChannel0, uv, log2(RADIUS));

    color.rgb += CHROMATIC * RADIUS * dFdx(mip.rgb) * vec3(-1, 0, 1);


    fragColor = color;
}
