void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord / iResolution.xy; // Normalize the coordinates
uv -= 0.5; // Move the origin to the center of the screen
uv.x *= iResolution.x / iResolution.y; // Fix the aspect ratio

float waveA = -.09*abs(sin((uv.x + iTime)*.2*25.)); // a moving sine wave with the negative values reflected, forming a series of spikes
float waveB = -.07*abs(sin((uv.x)*(10. + 6.*cos(iTime)))); // a similar wave, but instead of moving it stretches and squeezes
float waveMix = (waveA + waveB)/2.; // interpolation of the two 
float d = uv.y;
float c = smoothstep(waveMix, waveMix - 1.5/iResolution.y, d);

vec3 col = vec3(0., 0.5 - uv.y*uv.x, 1.); // bright blue

fragColor = vec4(vec3(col)*c, 1.0); // Output the color
}
