//Don't go below 1
#define brushSize 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    //uv array for texture sampling in moore's neighborhood
    vec2[9] uv;
    int z = 0;
    for (int x = 0; x < 3; x++) {
        for (int y = 0; y < 3; y++) {
            uv[z] = (fragCoord + vec2(x - 1, y - 1)) / iResolution.xy;
            z++;
        }
    }

    //textures for sampling (moore's neighborhood)
    vec4[9] tex;
    for (int i = 0; i < 9; i++) {
        tex[i] = texture(iChannel0, uv[i]);
    }

    //neighborhood values:
    //[2][5][8]
    //[1][4][7]
    //[0][3][6]

    //states:
    //on - 1., 0., 0.
    //dying - 0., 1., 0.
    //off - 0.

    //Get data from the moore neighborhood
    z = 0;
    for (int i = 0; i < 9; i++) {
        if (i == 4) {
            z = z;
        } else if (tex[i].xyz == vec3(1., 0., 0.)) {
                z++;
        }
    }

    //Dead to alive
    if (tex[4].xyz == vec3(0.) && z == 2) {
        tex[4].xyz = vec3(1., 0., 0.);
    }

    //Alive to dying
    else if (tex[4].xyz == vec3(1., 0., 0.)) {
        tex[4].xyz = vec3(0., 1., 0.);
    }

    //Dying to dead
    else if (tex[4].xyz == vec3(0., 1., 0.)) {
        tex[4].xyz = vec3(0.);
    }

    //use else statements, so the same pixel isn't checked multiple times
    //the way I understand the theory doesn't *quite* line up, but it works in practice

    //Make pixels
    if (iMouse.z > 0.) {
        if(distance(iMouse.xy, fragCoord) < brushSize) {
             tex[4].xyz = vec3(1.0, 0., 0.);
        }
    }

    //Output
    fragColor = vec4(tex[4]);
}
