#define Freqency 5.
#define SphereSize 0.25
#define SphereSmoothness 0.02
#define RotationAngle 30.
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

mat2 Rotate2D( float angle)
{
    float sn = sin(angle);
    float cs = cos(angle);

    return mat2(cs,-sn,
                  sn,cs);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    uv *= Rotate2D(45.);
    uv = fract(uv * Freqency) - 0.5;
    float dist = 1. - length(uv);
    float Radius = 1. - SphereSize;
    float res = smoothstep(Radius - SphereSmoothness,Radius + SphereSmoothness,dist);
    fragColor = vec4(res,res,res,1);

}
